python:
def lengthOfLongestSubsequence(nums: List[int], target: int) -> int:
    f = [0] + [-inf] * target
    s = 0
    for x in nums:
        s = min(s + x, target)
        for j in range(s, x - 1, -1):
            if f[j] < f[j - x] + 1:
                f[j] = f[j - x] + 1
    return f[-1] if f[-1] > 0 else -1

java:
public static int lengthOfLongestSubsequence(List<Integer> nums, int target) {
    int[] f = new int[target + 1];
    Arrays.fill(f, Integer.MIN_VALUE);
    f[0] = 0;
    int s = 0;
    for (int x : nums) {
        s = Math.min(s + x, target);
        for (int j = s; j >= x; j--) {
            f[j] = Math.max(f[j], f[j - x] + 1);
        }
    }
    return f[target] > 0 ? f[target] : -1;
}

cpp:
int lengthOfLongestSubsequence(vector<int> &nums, int target) {
    vector<int> f(target + 1, INT_MIN);
    f[0] = 0;
    int s = 0;
    for (int x : nums) {
        s = min(s + x, target);
        for (int j = s; j >= x; j--) {
            f[j] = max(f[j], f[j - x] + 1);
        }
    }
    return f[target] > 0 ? f[target] : -1;
}

