def minGroupsForValidAssignment(nums):
    cnt = {}
    for x in nums:
        cnt[x] = cnt.get(x, 0) + 1
    k = len(nums)
    for c in cnt.values():
        k = min(k, c)
    while True:
        ans = 0
        for c in cnt.values():
            if c // k < c % k:
                ans = 0
                break
            ans += (c + k) // (k + 1)
        if ans > 0:
            return ans
        k -= 1

java:
public static int minGroupsForValidAssignment(int[] nums) {
    Map<Integer, Integer> cnt = new HashMap<>();
    for (int x : nums) {
        cnt.merge(x, 1, Integer::sum);
    }
    int k = nums.length;
    for (int c : cnt.values()) {
        k = Math.min(k, c);
    }
    for (; ; k--) {
        int ans = 0;
        for (int c : cnt.values()) {
            if (c / k < c % k) {
                ans = 0;
                break;
            }
            ans += (c + k) / (k + 1);
        }
        if (ans > 0) {
            return ans;
        }
    }
}

cpp:
int minGroupsForValidAssignment(vector<int>& nums) {
    unordered_map<int, int> cnt;
    for (int x : nums) {
        cnt[x]++;
    }
    int k = min_element(cnt.begin(), cnt.end(), [](const auto& a, const auto& b) {
        return a.second < b.second;
    })->second;
    for (; ; k--) {
        int ans = 0;
        for (auto &[_, c] : cnt) {
            if (c / k < c % k) {
                ans = 0;
                break;
            }
            ans += (c + k) / (k + 1);
        }
        if (ans) {
            return ans;
        }
    }
}

