def constructProductMatrix(grid: List[List[int]]) -> List[List[int]]:
    MOD = 12345
    n, m = len(grid), len(grid[0])
    p = [[0] * m for _ in range(n)]
    suf = 1  
    for i in range(n - 1, -1, -1):
        for j in range(m - 1, -1, -1):
            p[i][j] = suf  
            suf = suf * grid[i][j] % MOD
    pre = 1  
    for i, row in enumerate(grid):
        for j, x in enumerate(row):
            p[i][j] = p[i][j] * pre % MOD  
            pre = pre * x % MOD
    return p

java:
public static int[][] constructProductMatrix(int[][] grid) {
    final int MOD = 12345;
    int n = grid.length, m = grid[0].length;
    int[][] p = new int[n][m];
    long suf = 1;
    for (int i = n - 1; i >= 0; i--) {
        for (int j = m - 1; j >= 0; j--) {
            p[i][j] = (int) suf;
            suf = suf * grid[i][j] % MOD;
        }
    }
    long pre = 1;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            p[i][j] = (int) (p[i][j] * pre % MOD);
            pre = pre * grid[i][j] % MOD;
        }
    }
    return p;
}

cpp:
vector<vector<int>> constructProductMatrix(vector<vector<int>> &grid) {
    const int MOD = 12345;
    int n = grid.size(), m = grid[0].size();
    vector<vector<int>> p(n, vector<int>(m));
    long long suf = 1;
    for (int i = n - 1; i >= 0; i--) {
        for (int j = m - 1; j >= 0; j--) {
            p[i][j] = suf;
            suf = suf * grid[i][j] % MOD;
        }
    }
    long long pre = 1;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            p[i][j] = p[i][j] * pre % MOD;
            pre = pre * grid[i][j] % MOD;
        }
    }
    return p;
}

