    return string(cnt1 - 1, '1') + string(s.length() - cnt1, '0') + '1';
}

python:
def shortestBeautifulSubstring(s: str, k: int) -> str:
    if s.count('1') < k:
        return ''
    ans = s
    cnt1 = left = 0
    for right, c in enumerate(s):
        cnt1 += int(c)
        while cnt1 > k or s[left] == '0':
            cnt1 -= int(s[left])
            left += 1
        if cnt1 == k:
            t = s[left: right + 1]
            if len(t) < len(ans) or len(t) == len(ans) and t < ans:
                ans = t
    return ans

java:
public static String shortestBeautifulSubstring(String S, int k) {
    if (S.replace("0", "").length() < k) {
        return "";
    }
    char[] s = S.toCharArray();
    String ans = S;
    int cnt1 = 0, left = 0;
    for (int right = 0; right < s.length; right++) {
        cnt1 += s[right] - '0';
        while (cnt1 > k || s[left] == '0') {
            cnt1 -= s[left++] - '0';
        }
        if (cnt1 == k) {
            String t = S.substring(left, right + 1);
            if (t.length() < ans.length() || t.length() == ans.length() && t.compareTo(ans) < 0) {
                ans = t;
            }
        }
    }
    return ans;
}

cpp:
string shortestBeautifulSubstring(string s, int k) {
    if (count(s.begin(), s.end(), '1') < k) {
        return "";
    }
    string ans = s;
    int cnt1 = 0, left = 0;
    for (int right = 0; right < s.length(); right++) {
        cnt1 += s[right] - '0';
        while (cnt1 > k || s[left] == '0') {
            cnt1 -= s[left++] - '0';
        }
        if (cnt1 == k) {
            string t = s.substr(left, right - left + 1);
            if (t.length() < ans.length() || t.length() == ans.length() && t < ans) {
                ans = move(t);
            }
        }
    }
    return ans;
}

python:
def addMinimum(s):
    t = 1
    for i in range(1, len(s)):
        t += s[i - 1] >= s[i]
    return t * 3 - len(s)

java:
public static int addMinimum(String word) {
    var s = word.toCharArray();
    int t = 1;
    for (int i = 1; i < s.length; ++i)
        if (s[i - 1] >= s[i])
            ++t;
    return t * 3 - s.length;
}

