}

python:
def maxIncreasingGroups(usageLimits: List[int]) -> int:
    ul = sorted(usageLimits)
    remain = 0
    require = 1
    for num in ul:
        remain += num
        if remain >= require:
            remain -= require 
            require += 1 
    return require - 1

java:
public static int maxIncreasingGroups(List<Integer> usageLimits) {
    Collections.sort(usageLimits);
    long remain = 0;
    int require = 1;
    for (int num : usageLimits) {
        remain += num;
        if (remain >= require) {
            remain -= require;
            require++;
        }
    }
    return require - 1;
}

