        remain += num;
        if (remain >= require) {
            remain -= require;
            require++;
        }
    }
    return require - 1;
}

python:
def lastVisitedIntegers(words: List[str]) -> List[int]:
    ans = []
    nums = []
    k = 0
    for s in words:
        if s[0] != 'p':  
            nums.append(int(s))
            k = 0
        else:
            k += 1
            ans.append(-1 if k > len(nums) else nums[-k])  
    return ans

java:
public static List<Integer> lastVisitedIntegers(List<String> words) {
    List<Integer> ans = new ArrayList<>();
    List<Integer> a = new ArrayList<>();
    int k = 0;
    for (String s : words) {
        if (s.charAt(0) != 'p') { 
            a.add(Integer.parseInt(s));
            k = 0;
        } else {
            ans.add(++k > a.size() ? -1 : a.get(a.size() - k)); 
        }
    }
    return ans;
}

cpp:
vector<int> lastVisitedIntegers(vector<string> &words) {
    vector<int> ans, a;
    int k = 0;
    for (auto &s: words) {
        if (s[0] != 'p') { 
            a.push_back(stoi(s));
            k = 0;
        } else {
            ans.push_back(++k > a.size() ? -1 : a[a.size() - k]); 
        }
    }
    return ans;
}

python:
def minOperations(s1, s2, x):
    if s1 == s2:
        return 0
    p = [i for i in range(len(s1)) if s1[i] != s2[i]]
    if len(p) % 2:
        return -1
    f0, f1 = 0, x
    for i in range(1, len(p)):
        new_f = min(f1 + x, f0 + (p[i] - p[i - 1]) * 2)
        f0, f1 = f1, new_f
    return f1 // 2

java:
public static int minOperations(String s1, String s2, int x) {
    if (s1.equals(s2)) {
        return 0;
    }
    List<Integer> p = new ArrayList<>();
    for (int i = 0; i < s1.length(); i++) {
        if (s1.charAt(i) != s2.charAt(i)) {
            p.add(i);
        }
    }
    if (p.size() % 2 != 0) {
        return -1;
    }
    int f0 = 0, f1 = x;
    for (int i = 1; i < p.size(); i++) {
        int newF = Math.min(f1 + x, f0 + (p.get(i) - p.get(i - 1)) * 2);
        f0 = f1;
        f1 = newF;
    }
    return f1 / 2;
}

cpp:
int minOperations(string s1, string s2, int x) {
    if (s1 == s2) return 0;
    vector<int> p;
    for (int i = 0; i < s1.size(); i++)
        if (s1[i] != s2[i])
            p.push_back(i);
    if (p.size() % 2) return -1;
    int f0 = 0, f1 = x;
    for (int i = 1; i < p.size(); i++) {
        int new_f = min(f1 + x, f0 + (p[i] - p[i - 1]) * 2);
        f0 = f1;
        f1 = new_f;
    }
    return f1 / 2;
}

