def minProcessingTime(processorTime: List[int], tasks: List[int]) -> int:
        processorTime.sort()
        tasks.sort(reverse=True)
        return max(p + t for p, t in zip(processorTime, tasks[::4]))

java:
public static int minProcessingTime(List<Integer> processorTime, List<Integer> tasks) {
    Collections.sort(processorTime);
    tasks.sort(Collections.reverseOrder());
    int ans = 0;
    for (int i = 0; i < processorTime.size(); i++) {
        ans = Math.max(ans, processorTime.get(i) + tasks.get(i * 4));
    }
    return ans;
}

cpp:
int minProcessingTime(vector<int>& processorTime, vector<int>& tasks) {
    sort(processorTime.begin(), processorTime.end());
    sort(tasks.begin(), tasks.end(), greater<int>());
    int ans = 0;
    for (int i = 0; i < processorTime.size(); i++) {
        ans = max(ans, processorTime[i] + tasks[i * 4]);
    }
    return ans;
}

