python:
def minSizeSubarray(nums: List[int], target: int) -> int:
    total = sum(nums)
    n = len(nums)
    ans = inf
    left = s = 0
    for right in range(n * 2):
        s += nums[right % n]
        while s > target % total:
            s -= nums[left % n]
            left += 1
        if s == target % total:
            ans = min(ans, right - left + 1)
    return ans + target // total * n if ans < inf else -1

java:
public static int minSizeSubarray(int[] nums, int target) {
    long total = 0;
    for (int x : nums) total += x;
    int n = nums.length;
    int ans = Integer.MAX_VALUE;
    int left = 0;
    long sum = 0;
    for (int right = 0; right < n * 2; right++) {
        sum += nums[right % n];
        while (sum > target % total) {
            sum -= nums[left++ % n];
        }
        if (sum == target % total) {
            ans = Math.min(ans, right - left + 1);
        }
    }
    return ans == Integer.MAX_VALUE ? -1 : ans + (int) (target / total) * n;
}

cpp:
int minSizeSubarray(vector<int> &nums, int target) {
    long long total = accumulate(nums.begin(), nums.end(), 0LL);
    int n = nums.size();
    int ans = INT_MAX;
    int left = 0;
    long long sum = 0;
    for (int right = 0; right < n * 2; right++) {
        sum += nums[right % n];
        while (sum > target % total) {
            sum -= nums[left++ % n];
        }
        if (sum == target % total) {
            ans = min(ans, right - left + 1);
        }
    }
    return ans == INT_MAX ? -1 : ans + target / total * n;
}

python:
def maximumTripletValue(nums: List[int]) -> int:
    n = len(nums)
    suf_max = [0] * (n + 1)
    for i in range(n - 1, 1, -1):
        suf_max[i] = max(suf_max[i + 1], nums[i])
    ans = pre_max = 0
    for j, x in enumerate(nums):
        ans = max(ans, (pre_max - x) * suf_max[j + 1])
        pre_max = max(pre_max, x)
    return ans

java:
public static long maximumTripletValue(int[] nums) {
    int n = nums.length;
    int[] sufMax = new int[n + 1];
    for (int i = n - 1; i > 1; i--) {
        sufMax[i] = Math.max(sufMax[i + 1], nums[i]);
    }
    long ans = 0;
    int preMax = nums[0];
    for (int j = 1; j < n - 1; j++) {
        ans = Math.max(ans, (long) (preMax - nums[j]) * sufMax[j + 1]);
        preMax = Math.max(preMax, nums[j]);
    }
    return ans;
}

