            for (int i = 0; i < n; i++) {
                if (stock[i] < com[i] * num) {
                    money += (com[i] * num - stock[i]) * cost[i];
                    if (money > budget) {
                        return false;
                    }
                }
            }
            return true;
        };
        int left = 0, right = mx + 1;
        while (left + 1 < right) { 
            int mid = (left + right) / 2;
            (check(mid) ? left : right) = mid;
        }
        ans = max(ans, left);
    }
    return ans;
}

