def minLengthAfterRemovals(nums):
    maxCnt = cnt = 1
    pre = nums[0]
    n = len(nums)
    for i in range(1, n):
        if pre == nums[i]:
            cnt += 1
        else:
            maxCnt = max(maxCnt, cnt)
            pre = nums[i]
            cnt = 1
    maxCnt = max(maxCnt, cnt)
    if maxCnt * 2 > n:
        return maxCnt - (n - maxCnt)
    else:
        return 0 if n % 2 == 0 else 1

