python:
def countInterestingSubarrays(nums, modulo, k):
    n = len(nums)
    ans = 0
    preSum = [0] * (n + 1)
    for i in range(n):
        preSum[i+1] = preSum[i] + (1 if nums[i] % modulo == k else 0)
    count_map = {}
    for i in range(n + 1):
        key = (preSum[i] % modulo - k + modulo) % modulo
        if key in count_map:
            ans += count_map[key]
        count_map[preSum[i] % modulo] = count_map.get(preSum[i] % modulo, 0) + 1
    return ans

java:
public static long countInterestingSubarrays(List<Integer> nums, int modulo, int k) {
    int n = nums.size();
    long ans = 0;
    int[] preSum = new int[n+1];
    for(int i=0;i<n;i++)
        preSum[i+1] = preSum[i] + (nums.get(i) % modulo == k?1:0);
    Map<Integer,Integer> map = new HashMap<>();
    for(int i=0;i<=n;i++){
        if(map.containsKey((preSum[i]%modulo - k + modulo)%modulo))
            ans += map.get((preSum[i]%modulo - k + modulo)%modulo);
        map.put(preSum[i]%modulo,map.getOrDefault(preSum[i]%modulo,0)+1);
    }
    return ans;
}

