        int sum = 0;
        for (int j = 0; j < n / 2; j++) {
            sum += s[j];
        }
        for (int j = n / 2; j < n; j++) {
            sum -= s[j];
        }
        if (sum == 0) {
            ans++;
        }
    }
    return ans;
}

