        sum += nums[i];
        cnt[nums[i]]++;
    }
    for (int i = k - 1; i < nums.size(); i++) {
        sum += nums[i]; 
        cnt[nums[i]]++;
        if (cnt.size() >= m)
            ans = max(ans, sum);
        int out = nums[i - k + 1];
        sum -= out; 
        if (--cnt[out] == 0)
            cnt.erase(out);
    }
    return ans;
}

