public static int minAbsoluteDifference(List<Integer> nums, int x) {
    var a = nums.stream().mapToInt(i -> i).toArray();
    int ans = Integer.MAX_VALUE, n = a.length;
    var s = new TreeSet<Integer>();
    s.add(Integer.MAX_VALUE); 
    s.add(Integer.MIN_VALUE / 2);
    for (int i = x; i < n; i++) {
        s.add(a[i - x]);
        int y = a[i];
        ans = Math.min(ans, Math.min(s.ceiling(y) - y, y - s.floor(y)));
    }
    return ans;
}

cpp:
int minAbsoluteDifference(vector<int> &nums, int x) {
    int ans = INT_MAX, n = nums.size();
    set<int> s = {INT_MIN / 2, INT_MAX}; 
    for (int i = x; i < n; i++) {
        s.insert(nums[i - x]);
        int y = nums[i];
        auto it = s.lower_bound(y); 
        ans = min(ans, min(*it - y, y - *prev(it))); 
    }
    return ans;
}

