def finalString(s: str) -> str:
    q = deque()
    tail = True
    for c in s:
        if c == 'i':
            tail = not tail  
        elif tail:  
            q.append(c)
        else:  
            q.appendleft(c)
    return ''.join(q if tail else reversed(q))

java:
public static String finalString(String s) {
    var q = new ArrayDeque<Character>();
    var tail = true;
    for (var c : s.toCharArray()) {
        if (c == 'i') tail = !tail;
        else if (tail) q.addLast(c);
        else q.addFirst(c);
    }
    var ans = new StringBuilder();
    for (var c : q) ans.append(c);
    if (!tail) ans.reverse();
    return ans.toString();
}

cpp:
string finalString(string s) {
    deque<char> q;
    bool tail = true;
    for (char c: s) {
        if (c == 'i') tail = !tail;
        else if (tail) q.push_back(c);
        else q.push_front(c);
    }
    return tail ? string(q.begin(), q.end()) : string(q.rbegin(), q.rend());
}

