def splitWordsBySeparator(words, separator):
    ans = []
    def func(str):
        t = ""
        for i in str:
            if i == ' ':
                continue
            if i == separator:
                if t:
                    ans.append(t)
                t = ""
            else:
                t += i
        if t:
            ans.append(t)

    for i in words:
        func(i)
    return ans

java:
public static List<String> splitWordsBySeparator(List<String> words, char separator) {
    List<String> rs = new ArrayList<>();
    StringBuffer sb = new StringBuffer();
    for (String word : words) {
        for (char c : word.toCharArray()) {
            if (c == separator) {
                if (sb.length() > 0) {
                    rs.add(sb.toString());
                    sb = new StringBuffer();
                }
            } else {
                sb.append(c);
            }
        }
        if (sb.length() > 0) {
            rs.add(sb.toString());
            sb = new StringBuffer();
        }
    }
    return rs;
}

