            if(i==' ')continue;
            if(i==separator){
                if(t.size())ans.push_back(t);
                t="";
            }else t+=i;
        }
        if(t.size())ans.push_back(t);
    };
    for(auto i:words){
        func(i);
    }
    return ans;
}

