    int mid = 0;
    while (low <= high) {
        mid = low + (high - low) / 2;
        if (arr.get(mid) < x) {
            high = mid - 1;
        } else if (arr.get(mid) > x) {
            low = mid + 1;
        } else {
            return mid + 1;
        }
    }
    return low + 1;
}
