    List<Integer> rankNums = new ArrayList<>(rankSet);
    Collections.sort(rankNums, Collections.reverseOrder());
    List<Integer> result = new ArrayList<>();
    for (int score : player) {
        int rank = bsearch(rankNums, score);
        result.add(rank);
    }
    return result;
}

