python:
def deleteNode(llist, position):
    if not llist:
        return 
    if position == 0:
        return llist.next
    current = previous = llist
    pointer = 1
    while pointer < position+1:
        previous = current
        current = current.next
        pointer += 1
    previous.next = current.next
    return llist

java:
public static  ListNode deleteNode(ListNode llist, int position) {
    if (llist == null) {
        return null;
    }
    if (position == 0) {
        return llist.next;
    }
    ListNode current = llist;
    for (int i = 0; current != null && i < position - 1; i++) {
        current = current.next;
    }
    if (current == null || current.next == null) {
        return llist;
    }
    ListNode next = current.next.next;
    current.next = next;
    return llist;
}

cpp:
ListNode* deleteNode(ListNode* llist, int position) {
    if (llist == nullptr) {
        return nullptr;
    }
    if (position == 0) {
        ListNode* newHead = llist->next;
        delete llist; 
        return newHead;
    }
    ListNode* current = llist;
    for (int i = 0; current != nullptr && i < position - 1; ++i) {
        current = current->next;
    }
    if (current == nullptr || current->next == nullptr) {
        return llist;
    }
    ListNode* nodeToDelete = current->next;
    current->next = current->next->next;
    delete nodeToDelete; 
    return llist;
}

