def cavityMap(grid):
    for k in range(1,len(grid)-1):
        i = grid[k]
        d = grid[k+1]
        u = grid[k-1]
        for j in range(1, len(grid)-1):
            if (i[j-1] < i[j] > i[j+1]) and (d[j] <i[j] > u[j]):
                x= list(grid[k])
                x[j] = "X"
                grid[k] = "".join(x)
    return grid

