def collectTheCoins(coins: List[int], edges: List[List[int]]) -> int:
    n = len(coins)
    g = [[] for _ in range(n)]
    for x, y in edges:
        g[x].append(y)
        g[y].append(x)  
    deg = list(map(len, g))  
    left_edges = n - 1      
    q = []
    for i, (d, c) in enumerate(zip(deg, coins)):
        if d == 1 and c == 0:  
            q.append(i)
    while q:
        left_edges -= 1  
        for y in g[q.pop()]:
            deg[y] -= 1
            if deg[y] == 1 and coins[y] == 0:  
                q.append(y)
    for i, (d, c) in enumerate(zip(deg, coins)):
        if d == 1 and c:  
            q.append(i)
    left_edges -= len(q)  
    for x in q:  
        for y in g[x]:
            deg[y] -= 1
            if deg[y] == 1:  
                left_edges -= 1  
    return max(left_edges * 2, 0)

java:
public static int collectTheCoins(int[] coins, int[][] edges) {
    int n = coins.length;
    List<Integer> g[] = new ArrayList[n];
    Arrays.setAll(g, e -> new ArrayList<>());
    var deg = new int[n];
    for (var e : edges) {
        int x = e[0], y = e[1];
        g[x].add(y);
        g[y].add(x); 
        deg[x]++;
        deg[y]++; 
    }
    int leftEdges = n - 1; 
    var q = new ArrayDeque<Integer>();
    for (int i = 0; i < n; i++) {
        if (deg[i] == 1 && coins[i] == 0) { 
            q.add(i);
        }
    }
    while (!q.isEmpty()) {
        leftEdges--; 
        for (int y : g[q.poll()]) {
            if (--deg[y] == 1 && coins[y] == 0) { 
                q.add(y);
            }
        }
    }
    for (int i = 0; i < n; i++) {
        if (deg[i] == 1 && coins[i] == 1) { 
            q.add(i);
        }
    }
    leftEdges -= q.size(); 
    for (int x : q) { 
        for (int y : g[x]) {
            if (--deg[y] == 1) { 
                leftEdges--; 
            }
        }
    }
    return Math.max(leftEdges * 2, 0);
}

