    for (int x: q) 
        for (int y: g[x])
            if (--deg[y] == 1) 
                left_edges--; 
    return max(left_edges * 2, 0);
}

python:
def matrixRotation(matrix, r):
    l, br, bc = 0, len(matrix), len(matrix[0])
    while l < min(br, bc) / 2:
        rr = r % (2 * (br + bc - l * 4 - 2))
        while (rr) != 0:
            rr -= 1
            buf = matrix[l][l]
            for j in range(l, bc - l - 1):
                matrix[l][j] = matrix[l][j + 1]
            for i in range(l, br - l - 1):
                matrix[i][bc - l - 1] = matrix[i + 1][bc - l - 1]
            for j in range(bc - l - 1, l, -1):
                matrix[br - l - 1][j] = matrix[br - l - 1][j - 1]
            for i in range(br - l - 1, l + 1, -1):
                matrix[i][l] = matrix[i - 1][l]
            matrix[l + 1][l] = buf
        l += 1
    return matrix

java:
public static List<List<Integer>> matrixRotation(List<List<Integer>> matrix, int r) {
	int l = 0;
	int br = matrix.size();
	int bc = matrix.get(0).size();
	while (l < Math.min(br, bc) / 2) {
		int rr = r % (2 * (br + bc - 4 * l - 2));
		while (rr-- > 0) {
			int buf = matrix.get(l).get(l);
			for (int j = l; j < bc - l - 1; ++j) {
				matrix.get(l).set(j, matrix.get(l).get(j + 1));
			}
			for (int i = l; i < br - l - 1; ++i) {
				matrix.get(i).set(bc - l - 1, matrix.get(i + 1).get(bc - l - 1));
			}
			for (int j = bc - l - 1; j > l; --j) {
				matrix.get(br - l - 1).set(j, matrix.get(br - l - 1).get(j - 1));
			}
			for (int i = br - l - 1; i > l + 1; --i) {
				matrix.get(i).set(l, matrix.get(i - 1).get(l));
			}
			matrix.get(l + 1).set(l, buf);
		}
		++l;
	}
	return matrix;
}

cpp:
vector<vector<int>> matrixRotation(vector<vector<int>>& matrix, int r) {
    int l = 0;
    int br = matrix.size();
    int bc = matrix[0].size();
    while (l < min(br, bc) / 2) {
        int rr = r % (2 * (br + bc - 4 * l - 2));
        while (rr--) {
            int buf = matrix[l][l];
            for (int j = l; j < bc - l - 1; ++j) {
                matrix[l][j] = matrix[l][j + 1];
            }
            for (int i = l; i < br - l - 1; ++i) {
                matrix[i][bc - l - 1] = matrix[i + 1][bc - l - 1];
            }
            for (int j = bc - l - 1; j > l; --j) {
                matrix[br - l - 1][j] = matrix[br - l - 1][j - 1];
            }
            for (int i = br - l - 1; i > l + 1; --i) {
                matrix[i][l] = matrix[i - 1][l];
            }
            matrix[l + 1][l] = buf;
        }
        ++l;
    }
    return matrix;
}

