def maximumJumps(nums: List[int], target: int) -> int:
    n = len(nums)
    f = [-inf] * n
    f[0] = 0
    for i in range(1, n):
        for j in range(i):
            if abs(nums[i] - nums[j]) <= target:
                f[i] = max(f[i], f[j] + 1)
    return -1 if f[-1] < 0 else f[-1]

java:
public static int maximumJumps(int[] nums, int target) {
    int n = nums.length;
    int[] dp = new int[n];
    Arrays.fill(dp, -1);
    dp[0] = 0;
    for (int i = 1; i < n; ++i) {
        for (int j = 0; j < i; ++j) {
            if (Math.abs(nums[i] - nums[j]) <= target && dp[j] != -1) {
                dp[i] = Math.max(dp[i], dp[j] + 1);
            }
        }
    }
    return dp[n - 1];
}

