        for(int j = 0; j < i; ++j)
        {
            if(abs(nums[i] - nums[j]) <= target)
                res = max(res, dp[j] + 1);
            dp[i] = res;
        }
    }
    return dp.back() < 0 ? -1 : dp.back();
}

