    int outOfRange = n, left = 0, right = 0;
    for (int i : id) {
        while (right < logs.length && logs[right][1] <= queries[i])
            if (cnt[logs[right++][0]]++ == 0)
                outOfRange--;
        while (left < logs.length && logs[left][1] < queries[i] - x)
            if (--cnt[logs[left++][0]] == 0)
                outOfRange++;
        ans[i] = outOfRange;
    }
    return ans;
}

