def minExtraChar(s: str, dictionary: List[str]) -> int:
    d = set(dictionary)
    n = len(s)
    f = [0] * (n + 1)
    for i in range(n):
        f[i + 1] = f[i] + 1  
        for j in range(i + 1):  
            if s[j:i + 1] in d:
                f[i + 1] = min(f[i + 1], f[j])
    return f[n]

java:
public static int minExtraChar(String s, String[] dictionary) {
    var set = new HashSet<String>(dictionary.length);
    for (var str : dictionary) set.add(str);
    int n = s.length();
    var f = new int[n + 1];
    for (int i = 0; i < n; i++) {
        f[i + 1] = f[i] + 1;
        for (int j = 0; j < i+1; j++) {
            if (set.contains(s.substring(j, i + 1))) {
                f[i + 1] = Math.min(f[i + 1], f[j]);
            }
        }
    }
    return f[n];
}

