def dfs(x: int, fa: int, g, coins, k) -> List[int]:
    res1 = [0] * 14
    res2 = [0] * 14
    for y in g[x]:
        if y == fa: continue
        r = dfs(y, x, g, coins, k)
        for j, v in enumerate(r):
            res1[j] += v
            if j < 13:
                res2[j] += r[j + 1]
    for j, (r1, r2) in enumerate(zip(res1, res2)):
        res1[j] = max(r1 + (coins[x] >> j) - k, r2 + (coins[x] >> (j + 1)))
    return res1

