    merged = []
    p1, p2 = 0, 0
    while True:
        if p1 == len(nums1):
            merged.extend(nums2[p2:])
            break
        if p2 == len(nums2):
            merged.extend(nums1[p1:])
            break
        if nums1[p1] < nums2[p2]:
