    inorder(root1, nums1);
    inorder(root2, nums2);
    List<Integer> merged = new ArrayList<>();
    int p1 = 0, p2 = 0;
    while (true) {
        if (p1 == nums1.size()) {
            merged.addAll(nums2.subList(p2, nums2.size()));
            break;
        }
        if (p2 == nums2.size()) {
            merged.addAll(nums1.subList(p1, nums1.size()));
            break;
        }
        if (nums1.get(p1) < nums2.get(p2)) {
            merged.add(nums1.get(p1++));
        } else {
            merged.add(nums2.get(p2++));
        }
    }
    return merged;
}

