def longestSquareStreak(nums: List[int]) -> int:
    ans, s = 0, set(nums)
    for x in s:
        cnt = 0
        while x in s:
            cnt += 1
            x *= x
        ans = max(ans, cnt)
    return ans if ans > 1 else -1

java:
public static int longestSquareStreak(int[] nums) {
    int ans = 0;
    Set<Integer> s = new HashSet<>();
    for (int num : nums) {
        s.add(num);
    }
    for (int x : s) {
        int cnt = 0;
        while (s.contains(x)) {
            cnt += 1;
            if (x > Math.sqrt(Integer.MAX_VALUE)) break;
            x *= x;
        }
        ans = Math.max(ans, cnt);
    }
    return ans > 1 ? ans : -1;
}

