int longestSquareStreak(vector<int>& nums) {
    int ans = 0;
    unordered_set<int> s(nums.begin(), nums.end());
    for (int x : s) {
        int cnt = 0;
        while (s.find(x) != s.end()) {
            cnt += 1;
            if (x > std::sqrt(INT_MAX)) break;
            x *= x;
        }
        ans = max(ans, cnt);
    }
    return ans > 1 ? ans : -1;
}

python:
def appendCharacters(s: str, t: str) -> int:
    j, m = 0, len(t)
    for c in s:
        if c == t[j]:
            j += 1
            if j == m: return 0
    return m - j

java:
public static int appendCharacters(String s, String t) {
    int j = 0, m = t.length();
    for (char c : s.toCharArray()) {
        if (c == t.charAt(j)) {
            j++;
            if (j == m) return 0;
        }
    }
    return m - j;
}

